mapper.select:

SELECT uid, name, state, fail_upw,
	(
		SELECT COUNT(*) 
		FROM member_info 
		WHERE uid = #{uid} 
		AND upw = #{upw}
    ) AS check_no
FROM member_info
WHERE uid = #{uid}

mapper.update:

UPDATE member_info SET 
	state = #{state},
    fail_upw = #{fail_upw}
WHERE uid = #{uid}


UPDATE member_info SET 
	upw = #{upw} 
WHERE uid = #{uid}

  		UPDATE file_list SET 
			del_yn = 'Y'
		WHERE seq = #{seq}
